import React, { Component } from "react";
import Countries from "./country"

class Forms extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      birthday: "",
      country: "",
      diet: "",
      complete: false
    }
  }
  createOption = props => {
    let options = [];
    for (let i = 0; i < Countries.length; i++) {
      options.push(<option key={i}>{Countries[i].name}</option>);
    }
    return options;
  };

  clickSubmit(event) {
    event.preventDefault();
    this.setState;
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name] : event.target.value
    })
    console.log(this.state)
  }

  render() {
    return (
      <div>
        <h1>Mars Trip</h1>
        <form onChange={this.handleChange}>
          <h2> Section 1 </h2>
          <label htmlFor="name">Full Name: </label>
          <input
            placeholder="First Name Last Name"
            name="name"
            defaultValue = {
              this.state.name
            }
          />
          <br />
          <label htmlFor="birthday">Birthday: </label>
          <input
            type="date"
            name="birthday"
            defaultValue = {this.state.birthday}
          />
          <br />
          <label htmlFor="country">Country Of Origin: </label>
          <select name="country" defaultValue={this.state.country} >
          <option disabled value = {this.state.country}></option>
          {this.createOption()}</select>
          <br />
          <label htmlFor="diet">Diet: </label>
          <select name = "diet" defaultValue = {this.state.diet}>
            <option disabled value={this.state.diet}></option>
            <option>Omnivore</option>
            <option>Vegetarian</option>
            <option>Vegan</option>
          </select>
          <br></br>
          <button onClick={this.clickSubmit}>Next Section></button>
          <br />
        </form>
      </div>
    );
  }
}

export default Forms;
